// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC1271.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "../interfaces/IERC6551Account.sol";
import "../lib/ERC6551AccountLib.sol";

contract TokenBoundAccount is IERC165, IERC1271, IERC6551Account {
    uint256 public nonce;

    receive() external payable {}

    function executeCall(
        address to,
        uint256 value,
        bytes calldata data
    ) external payable returns (bytes memory result) {
        require(msg.sender == owner(), "Not token owner");

        ++nonce;

        emit TransactionExecuted(to, value, data);
        bool success;
        (success, result) = to.call{value: value}(data);

        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        } // traansaction
    }

    function token() external view returns (uint256, address, uint256) {
        return ERC6551AccountLib.token();
    } //see NFT in wallet

    function owner() public view returns (address) {
        (uint256 chainId, address tokenContract, uint256 tokenId) = this
            .token();
        if (chainId != block.chainid) return address(0);

        return IERC721(tokenContract).ownerOf(tokenId);
    } //returns owner wallet

    function supportsInterface(bytes4 interfaceId) public pure returns (bool) {
        return (interfaceId == type(IERC165).interfaceId ||
            interfaceId == type(IERC6551Account).interfaceId);
    }

    function balance() public view returns (uint256) {
        return address(this).balance;
    }

    function isValidSignature(
        bytes32 hash,
        bytes memory signature
    ) external view returns (bytes4 magicValue) {
        bool isValid = SignatureChecker.isValidSignatureNow(
            owner(),
            hash,
            signature
        );

        if (isValid) {
            return IERC1271.isValidSignature.selector;
        }
        return "";
    } // sign

    function approveTransfer(address newOwner) external {
        require(msg.sender == owner(), "Not token owner");

        (uint256 chainId, address tokenContract, uint256 tokenId) = this
            .token();

        // Approve the new owner to transfer the token
        IERC721(tokenContract).approve(newOwner, tokenId);
    }

    function transferOwnership(address newOwner) external {
        require(msg.sender == owner(), "Not token owner");
        require(newOwner != address(0), "New owner is the zero address");

        (uint256 chainId, address tokenContract, uint256 tokenId) = this
            .token();

        // Call the ERC721 contract to transfer the token to the new owner
        IERC721(tokenContract).transferFrom(owner(), newOwner, tokenId);
    }
}

